  `invoice_content` varchar(200) NOT NULL COMMENT '发票内容',
  `freight_fee` bigint(8) NOT NULL DEFAULT 0 COMMENT '运费',
  `use_coupon` bigint(8) NOT NULL DEFAULT 0 COMMENT '使用优惠劵优惠价格',
  `use_coupon_id` varchar(50) DEFAULT '' COMMENT '优惠券id',
  `use_card` bigint(8) NOT NULL DEFAULT 0 COMMENT '使用e卡优惠价格',
  `use_card_id` varchar(50) DEFAULT '' COMMENT 'e卡 卡号',
  `use_balance` bigint(10) NOT NULL DEFAULT 0 COMMENT '使用余额支付多少钱',
  `promotion_id` bigint(14) DEFAULT NULL COMMENT '促销规则ID',
  `promotion_price` bigint(14) NOT NULL DEFAULT 0 COMMENT '订单促销优惠的价格',
  `ware_total_price` bigint(14) NOT NULL DEFAULT 0,
  `total_price` bigint(14) NOT NULL DEFAULT 0 COMMENT '订单优惠前总价',
  `order_price` bigint(14) NOT NULL DEFAULT 0 COMMENT '订单最终实付价格',
