  `return_status` int(11) DEFAULT NULL COMMENT '逆向流程状态',
  `refund_flag` int(11) DEFAULT NULL COMMENT '退款标示',
  `order_pay_time` datetime DEFAULT NULL COMMENT '订单支付完成时间',
  `order_cancel_time` datetime DEFAULT NULL COMMENT '订单取消时间',
  `order_complete_time` datetime DEFAULT NULL COMMENT '定单完成时间\n',
  `user_ip` varchar(15) NOT NULL COMMENT '用户下单Ip地址\n',
  `order_sn` varchar(50) DEFAULT NULL COMMENT '订单流水号,目前仅为线下订单使用',
  `sent_flag` int(11) DEFAULT -1 COMMENT '订单下发状态(-1:未下发, 1:已下发)',
