  `display_status` tinyint(2) DEFAULT 1 COMMENT '订单存在状态，1/-1，用户删除订单时使用',
  `payment_trade_num` varchar(50) DEFAULT NULL COMMENT '第三方平台交易号码',
  `order_sku_type` tinyint(4) DEFAULT NULL COMMENT '订单商品类型(1:标品,2:散卖)',
  `invoice_type` varchar(50) DEFAULT NULL COMMENT '发票类型',
  `order_create_time` datetime DEFAULT NULL COMMENT '订单下单时间',
  `shipment_time` varchar(40) DEFAULT NULL COMMENT '配送/自提时间，格式为HH:mm-HH:mm',
