  `return_status` int(11) DEFAULT NULL COMMENT '逆向流程状态',
  `refund_flag` int(11) DEFAULT NULL COMMENT '退款标示',
  `order_pay_time` datetime DEFAULT NULL COMMENT '订单支付完成时间',
  `order_cancel_time` datetime DEFAULT NULL COMMENT '订单取消时间',
  `order_complete_time` datetime DEFAULT NULL COMMENT '定单完成时间\n',
  `user_ip` varchar(15) NOT NULL COMMENT '用户下单Ip地址\n',
  `order_sn` varchar(50) DEFAULT NULL COMMENT '订单流水号,目前仅为线下订单使用',
  `sent_flag` int(11) DEFAULT -1 COMMENT '订单下发状态(-1:未下发, 1:已下发)',
  `remarks` varchar(150) DEFAULT NULL COMMENT '定单备注',
  `properties` varchar(1000) DEFAULT NULL COMMENT 'json数据串',
  `created` datetime NOT NULL,
  `modified` datetime NOT NULL,
  `yn` tinyint(1) NOT NULL DEFAULT 1,
  `vender_id` int(11) DEFAULT NULL COMMENT '商家ID',
  `vender_name` varchar(45) DEFAULT NULL COMMENT '商家名称',
  `erp_store_id` int(11) DEFAULT NULL COMMENT '门店ID（主键）',
  `order_main_mark` varchar(50) DEFAULT NULL COMMENT '系统调用标识（如活动系统标识或者推广渠道标识）',
  `self_code` varchar(10) DEFAULT NULL COMMENT '创建自提订单时生成的提货码，暂定为6位随机数字',
  `display_status` tinyint(2) DEFAULT 1 COMMENT '订单存在状态，1/-1，用户删除订单时使用',
  `payment_trade_num` varchar(50) DEFAULT NULL COMMENT '第三方平台交易号码',
  `order_sku_type` tinyint(4) DEFAULT NULL COMMENT '订单商品类型(1:标品,2:散卖)',
  `invoice_type` varchar(50) DEFAULT NULL COMMENT '发票类型',
  `order_create_time` datetime DEFAULT NULL COMMENT '订单下单时间',
  `shipment_time` varchar(40) DEFAULT NULL COMMENT '配送/自提时间，格式为HH:mm-HH:mm',
  `partition_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `order_webuser_id_idx` (`webuser_id`),
  KEY `order_sn_idx` (`order_sn`) USING BTREE,
  KEY `shop_id` (`shop_id`,`created`,`order_pay_time`),
  KEY `modified_idx` (`modified`),
  KEY `order_type_status` (`order_type`,`order_status`),
  KEY `order_status` (`order_status`),
  KEY `partition_id` (`partition_id`),
  KEY `id` (`id`,`webuser_id`)
) ENGINE=TokuDB AUTO_INCREMENT=25559713 DEFAULT CHARSET=utf8;


